import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import { CircleSlider } from "react-circle-slider";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { value1: 0, value2: 0 };
  }

  // CHAMADO A APÓS O RENDER PARA GARANTIR QUE OS LEDS SERÃO SETADOS
  /*componentDidMount() {
    fetch("/led1")
      .then(response => response.text())
      .then(state => this.setState(state));
    fetch("/led4")
      .then(response => response.text())
      .then(state => this.setState(state));
  }*/

  handleChange = value => {
    fetch("/led1", { method: "PUT", body: value })
      .then(response => response.text())
      .then(this.setState({ value }));
  };

  handleChangeRange = event => {
    this.setState({
        value: event.target.valueAsNumber,
    });
  };



  render() {
    return (
      <div className="App">
        <header className="App-header">
          <body data-spy="scroll" data-target="#lambda-navbar" data-offset="0">
            <nav
              class="navbar navbar-expand-md navbar-dark navbar-transparent fixed-top sticky-navigation"
              id="lambda-navbar"
            >
              <a class="navbar-brand" href="">
                Decoled App
              </a>
              <button
                class="navbar-toggler navbar-toggler-right border-0"
                type="button"
                data-toggle="collapse"
                data-target="#navbarCollapse"
                aria-controls="navbarCollapse"
                aria-expanded="false"
                aria-label="Toggle navigation"
              >
                <span data-feather="menu"></span>
              </button>
              <div class="collapse navbar-collapse" id="navbarCollapse">
                <ul class="navbar-nav ml-auto">
                  <li class="nav-item">
                    <a class="nav-link page-scroll" href="#principal">
                      Sala
                    </a>
                  </li>
                  <li class="nav-item">
                    <a class="nav-link page-scroll" href="#hall">
                      Hall
                    </a>
                  </li>
                </ul>
              </div>
            </nav>
          </body>

          <section class="App-trans" id="principal">
            <img src={logo} className="App-logo" alt="logo" />
            <h1 className="App-title">Sala</h1>
            <CircleSlider
              onChange={this.handleChange}
              value={this.state.value1}
              size={150}
              showTooltip={true}
              gradientColorFrom="#009f5c"
              gradientColorTo="#006b5c"
              showPercentage={true}
              tooltipColor="#6ab6e1"
              stepSize={10}
            />
          </section>
          <section class="App-trans" id="hall">
            <img src={logo} className="App-logo" alt="logo" />
            <h1 className="App-title">Hall</h1>
            <CircleSlider
              onChange={this.handleChange}
              value={this.state.value4}
              size={140}
              showTooltip={true}
              gradientColorFrom="#009f5c"
              gradientColorTo="#006b5c"
              showPercentage={true}
              tooltipColor="#6ab6e1"
              stepSize={10}
            />
          </section>
        </header>
      </div>
    );
  }
}

export default App;
